import java.util.*;
import java.math.*;


class Euler08{
	public static String s="";

	public static long sliding_Window(ArrayList<Integer> arr, int size,int counter){
		long maxmul=1;
		for(int i=0;i<size-counter+1;i++ ){
			long mul=1;
			for (int j=0;j<counter;j++){
				mul=mul*arr.get(i+j);
				//System.out.println(mul);
			}
			if(mul>maxmul){
				maxmul=mul;
			}
			
		}
		return maxmul;
	}

	public static void main(String[] args){
		long startTime = System.nanoTime();
		ArrayList<Integer> intArray= new ArrayList<Integer>();

		for(char obj:s.toCharArray()){
			intArray.add( Character.getNumericValue(obj) );
		}
		//System.out.println(intArray.toString());
		long result= sliding_Window(intArray,intArray.size(),13);
		System.out.println(result);
		long endTime = System.nanoTime();

		long timeElapsed = endTime - startTime;

		System.out.println("Execution time in nanoseconds: " + timeElapsed);
		System.out.println("Execution time in milliseconds: " + timeElapsed / 1000000);
	}
}

